import React, { useState } from 'react';
import DestinationInput from './DestinationInput';
import DateRangePicker from './DateRangePicker';
import GuestPicker from './GuestPicker';

const HomeSearchBar = () => {
  const [isOpen, setIsOpen] = useState(false); // Estado para manejar la visibilidad

  const toggleSearchBar = () => setIsOpen(!isOpen); // Función para cambiar la visibilidad

  return (
    <div className="home-search-bar">
      <button className="toggle-button" onClick={toggleSearchBar}>
        {isOpen ? 'Cerrar' : 'Buscar'}
      </button>
      {isOpen && (
        <>
          <DestinationInput />
          <DateRangePicker />
          <GuestPicker />
          <button className="search-button">Buscar</button>
        </>
      )}
    </div>
  );
};

export default HomeSearchBar;
