import React, { useState } from 'react';
import DestinationInput from './DestinationInput';
import DateRangePicker from './DateRangePicker';
import GuestPicker from './GuestPicker';

const HomeSearchBar = () => {
  const [isOpen, setIsOpen] = useState(false); // Estado para controlar la visibilidad

  return (
    <div className="home-search-bar">
      <button onClick={() => setIsOpen(!isOpen)} className="toggle-form-button">
        {isOpen ? 'Cerrar' : 'Buscar'} {/* Cambia el texto seg√∫n el estado */}
      </button>
      {isOpen && ( // Solo renderiza el contenido si isOpen es true
        <>
          <DestinationInput />
          <DateRangePicker />
          <GuestPicker />
          <button className="search-button">Buscar</button>
        </>
      )}
    </div>
  );
};

export default HomeSearchBar;
